pragma solidity ^0.4.23;

interface Regulator {
    function checkValue(uint amount) public returns (bool);
    function loan() public returns (bool);
}

contract Bank is Regulator {
    // Internal attribute
    // uint internal myInternalValue; // This can be accessed in Inheritance but not accessible from outside
                                      // access modifier: public private internal = public private protected
    uint private value;
    address private owner;

    // Owner based functionality
    modifier ownerFunc { 
        require(owner == msg.sender); // Exception: require, assert, revert, throw
        _; // essence of your function being executed
    }

    function Bank(uint amount) public { // constructor
        value = amount;
        owner = msg.sender;
    }

    function deposit (uint amount) public ownerFunc {
        value += amount;
    }

    function withdraw (uint amount) public ownerFunc {
        if (checkValue(amount)) {
            value -= amount;
        }    
    }

    function balance() public returns (uint) {
        return value;
    }

    // function loan() public returns (bool); // abstraction: to be further implemented in inheritance child

    function checkValue(uint amount) public returns (bool) {
        return value >= amount;
    }

    function loan() public returns (bool) {
        return value > 0;
    }

}

